export default function EmojiPicker({ onEmojiSelect, onClose }: EmojiPickerProps) {
  const colorScheme = useColorScheme();
  const isDark = colorScheme === 'dark';
  const [selectedCategory, setSelectedCategory] = useState<keyof typeof EMOJI_CATEGORIES>('smileys');

  return (
    <View style={[styles.container, { backgroundColor: isDark ? colors.cardDark : colors.card }]}>
      <View style={[styles.header, { borderBottomColor: isDark ? colors.borderDark : colors.border }]}>
        <Text style={[styles.title, { color: isDark ? colors.textDark : colors.text }]}>
          Pick an Emoji
        </Text>
        <TouchableOpacity onPress={onClose} style={styles.closeButton}>
          <Text style={[styles.closeText, { color: colors.primary }]}>Done</Text>
        </TouchableOpacity>
      </View>

      <View style={styles.categories}>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          {Object.entries(EMOJI_CATEGORIES).map(([key, category]) => (
            <TouchableOpacity
              key={key}
              style={[
                styles.categoryButton,
                selectedCategory === key && {
                  backgroundColor: isDark ? colors.backgroundAltDark : colors.backgroundAlt,
                },
              ]}
              onPress={() => setSelectedCategory(key as keyof typeof EMOJI_CATEGORIES)}
            >
              <Text style={styles.categoryIcon}>{category.icon}</Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>

      <ScrollView style={styles.emojiGrid} showsVerticalScrollIndicator={false}>
        <View style={styles.emojiContainer}>
          {EMOJI_CATEGORIES[selectedCategory].emojis.map((emoji, index) => (
            <TouchableOpacity
              key={index}
              style={styles.emojiButton}
              onPress={() => onEmojiSelect(emoji)}
            >
              <Text style={styles.emoji}>{emoji}</Text>
            </TouchableOpacity>
          ))}
        </View>
      </ScrollView>
    </View>
  );
}

